// import { color } from 'd3-color';
import React from 'react';
// import { Link } from 'react-router-dom';

class WineInfo extends React.Component {

    constructor(props){
        super(props)
        // this.connectWalletHandler = this.connectWalletHandler.bind(this)
        this.wineName = this.wineName.bind(this)
        this.wineDescription = this.wineDescription.bind(this)
        this.wineNumber = this.wineNumber.bind(this)
        this.wineProgressBar = this.wineProgressBar.bind(this)
        // this.WineInfo = this.WineInfo.bind(this)
        this.wineBuySection = this.wineBuySection.bind(this)
        this.handleMintClick = this.handleMintClick.bind(this)
        this.state = {
            mintClicked : true,
        }
    }

    wineName(){
       if (!this.state.mintClicked){
        return(

            <div className="shoeName">

            <div>
              <h1 className="big">Chardonnay</h1>
              <span className="new">FEATURED</span>
            </div>
            <h4 className="small">Dry Red Wine 750ml</h4>
          </div>
        )
       }
    }

    handleMintClick(){

        if(this.state.mintClicked){
            alert(true)
        }
    }

    wineNumber(){
        if(!this.state.mintClicked){
            return(
                <div className="size-container">
          <h3 className="title">Wines to Mint</h3>
          <div className="sizes">
            <span className="size">+</span>
            <span className="size active">0</span>
            <span className="size">-</span>
            {/* <span className="size">10</span>
            <span className="size">11</span> */}
          </div>
        </div>
            )
        }
    }

    wineBuySection(){
       if(!this.state.mintClicked){
        return(
            <div className="buy-price">
      <a href="/#" className="buy">
        <i className="fas fa-shopping-cart" onClick={this.handleMintClick()}></i>Mint
      </a>
      <div className="price">
        <i className="fas fa-dollar-sign"></i>
        <h1>0.5ETH</h1>
      </div>
    </div>
        )
       }
    }

    wineDescription(){
        if(!this.state.mintClicked){
            return(
                <div className="description">
                <h3 className="title">Description</h3>
                <p className="text">
                Red. This is Spain 2019s most planted and highly-prized red variety. Wines range in style from rose to red, but Tempranillo is perhaps most known by its two champion regions of Rioja and Ribera del Duero.
                </p>
              </div>
            )
        }
    }

    wineProgressBar(){
        const containerStyles = {
            height: 20,
            width: '100%',
            // backgroundColor: "#e0e0de",
            borderRadius: 40,
            // margin: 50
          }

          const fillerStyles = {
            height: '100%',
            width: `${50}%`,
            backgroundColor: "#000000",
            borderRadius: 'inherit',
            textAlign: 'right'
          }

          const labelStyles = {
            padding: 4,
            color: 'white',
            fontWeight: 'bold'
          }
          if(!this.state.mintClicked){
            return(
                <div className="color-container">
                <h3 className="title-wine">300 Minted</h3>
                <div style={containerStyles}>
                <div style={fillerStyles}>
                  <span style={labelStyles}>{`${50}%`}</span>
                </div>
              </div>
              </div>
              )
          }

    }

    render() {

        return (
            <div className="info">
                {!this.state.mintClicked  && <div className="shoeName">

                <div>
                  <h1 className="big">Chardonnay</h1>
                  <span className="new">FEATURED</span>
                </div>
                <h4 className="small">Dry Red Wine 750ml</h4>
              </div>}


                {/* {this.wineName()} */}
                {/* {this.wineDescription()}
                {this.wineProgressBar()}
                {this.wineNumber()}
                {this.wineBuySection()} */}

            </div>
        )
    }
}

export default WineInfo;
