      this.useEffect()

  }

    useEffect(){

      if (window.ethereum) {
        this.handleInit()
        // console.log("going in if")
      } else {
        window.addEventListener('ethereum#initialized', this.handleInit, {
          once: true,
        })
        setTimeout(this.handleInit, 3000) // 3 seconds
      }

    }

    render() {

        return (
          <div>
          <Header></Header>
        <div className="App">

          <Home />
        </div>
       </div>
        )
    }
}

export default App;
